import Head from 'next/head'
import Layout from '../../components/layout'
import Date from '../../components/date'
import { getSortedProjectData } from '../../lib/projects'
import Link from 'next/link'
import { motion } from 'framer-motion'

export default function Portfolio({ allProjectData }) {

  return (
    <motion.div exit={{ opacity: 0 }} initial={{ opacity: 0 }} animate={{ opacity: 1 }}>
      <Layout portfolioHome>
        <Head></Head>

        <div className="mw9 center ph3-ns">
          <div class="cf ph2-ns">
            {allProjectData.map(({ id, date, title, description, role, image }) => (
              <div className="fl w-100 w-50-ns pa2 project grow" key={id}>
                    <Link href="/portfolio/[id]" as={`/portfolio/${id}`}><a><img className="w-100" src={image}></img></a></Link>
                    <div className="absolute left-1 bottom-1 right-1 w-100 bg-white outline color pa2 reveal">
                      <Link href="/portfolio/[id]" as={`/portfolio/${id}`}>
                        <a className="link color underline">
                          <h1 className="f5 f4-ns  mv0">
                            {title}
                            <Date dateString={date}></Date>
                          </h1></a>
                      </Link>
                      <h6 className="mv2 fw4 f6">{role}</h6>
                  </div>
                </div>

            ))}
          </div>
        </div>

      </Layout>
    </motion.div >

  )
}
export async function getStaticProps() {
  const allProjectData = getSortedProjectData()
  return {
    props: {
      allProjectData
    }
  }
}
