import Head from 'next/head'
import Layout from '../../components/layout'
import { getSortedProjectData } from '../../lib/projects'
import Link from 'next/link'
import { motion } from 'framer-motion'

export default function Portfolio({ allProjectData }) {

  return (
    <motion.div exit={{ opacity: 0 }} initial={{ opacity: 0 }} animate={{ opacity: 1 }}>
      <Layout portfolioHome>
        <Head></Head>

        <div className="mw9 center ph3-ns">
          <div class="cf ph2-ns">
            {allProjectData.map(({ id, title, description, role, image }) => (
              <div className="fl w-100 w-50-ns outline pa2 project" key={id}>
                <div className="bg-white">

                  <img className="w-100 bb b--black-05" src={image}></img>
                  <div className="pa2 ">
                    <h6 className="mv2 fw4">{role}</h6>
                    <Link href="/portfolio/[id]" as={`/portfolio/${id}`}>
                      <a className="link color underline"><h1 className="f4 f3-ns mv0">{title}</h1></a>
                    </Link>
                  </div>
                </div>
              </div>

            ))}
          </div>
        </div>

      </Layout>
    </motion.div >

  )
}
export async function getStaticProps() {
  const allProjectData = getSortedProjectData()
  return {
    props: {
      allProjectData
    }
  }
}
